let filter_id=document.getElementById('filter_id');
if(filter_id){
    filter_id.addEventListener('change',function(){
    let companyId=this.value;
    // window.location.href=window.location.href + alert(companyId)

    window.location.href=window.location.href.split('?')[0] + '?company_id='+companyId
})
}

let clearBtn=document.getElementById('refresh')
if(clearBtn){
    clearBtn.addEventListener('click',function(){
    let input=document.getElementById('search');
    let  search=document.getElementById('filter_id');
    if(input)input.value='';
    if(search)search.selectedIndex=0

    window.location.href=window.location.href.split('?')[0];
})
}

// 
document.querySelectorAll('.btn-delete').forEach((button)=>{
    button.addEventListener('click',function(event){
        event.preventDefault()
        if(confirm('Are you sure?')){
            let action=this.getAttribute('href');
            let form=document.getElementById('form-delete');
            form.setAttribute('action',action);
            form.submit();
        }
    })
})